import React, { useEffect, useReducer } from "react";
import { BrowserRouter, Switch, Redirect } from "react-router-dom";
import { Provider } from 'react-redux';

import AdminLayout from "layouts/Admin/Admin.js";
import LoginLayout from "layouts/Login/Login.js";

import { store } from "./store/store";
import { authReducer } from "./auth/authReducer";
import { AuthContext } from "./auth/AuthContext";
import { PrivateRoute } from "./routers/PrivateRoute";
import { PublicRoute } from "./routers/PublicRoute";

const init = () => {
    return JSON.parse(localStorage.getItem('user')) || { logeado: false };
};

export const TicsApp = () => {

    const [user, dispatch] = useReducer(authReducer, {}, init);

    useEffect(() => {
        localStorage.setItem('user', JSON.stringify(user));
    }, [user]);

    return (
        <Provider store={store}>
            <BrowserRouter>
                <AuthContext.Provider value={{ user, dispatch }}>
                    <Switch>
                        <PublicRoute
                            path="/login"
                            isAuthenticated={user.logeado}
                            component={(props) => <LoginLayout {...props} />} />
                        <PrivateRoute
                            path="/admin"
                            isAuthenticated={user.logeado}
                            component={(props) => <AdminLayout {...props} />} />
                        <Redirect from="/" to="/admin/Alarm" />
                    </Switch>
                </AuthContext.Provider>
            </BrowserRouter>
        </Provider>
    )
};
