import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Card, CardHeader, CardBody, Row, Col } from "reactstrap";
import swal from 'sweetalert';
// reactstrap components
import {
  Table,
} from "reactstrap";

import { clienteAxios } from "helpers/axios";
import { ListVecinoFila } from "./ListVecinoFila";

function ListVecino() {

  const { id } = useSelector(state => state.auth);

  const [vecinos, setVecinos] = useState([]);

  // Consulta a la API
  useEffect(() => {
    fetchVecinos();
  }, []);

  const fetchVecinos = () => {
    console.log('consultando vecinos...');
    let request = new Request('http://localhost:4000/api/vecino/getallvecinos', {
      method: 'GET',
      mode: 'cors',
      credentials: 'omit',
      referrerPolicy: 'no-referrer',
      headers: {
        'x-token': localStorage.getItem('token') || ''
      }
    });
    fetch(request)
      .then(response => response.json())
      .then(dataJSON => {
        const { data } = dataJSON;
        setVecinos(data);
      })
      .catch(err => {
        console.error(err);
      })
  };

  const handleInputDelete = (id_veci) => {
    swal("Seguro que desea eliminar al vecino? Esta acci√≥n no puede revertirse...", {
      buttons: {
        cancel: "Cancelar",
        aceptar: {
          text: "Borrar",
          value: "borrar",
        },
      },
    })
      .then(async (value) => {
        switch (value) {
          case "borrar":
            // codigo para borrar
            try {
              const resp = await clienteAxios.delete(`/api/vecino/deleteVecino/${id_veci}`, {
                headers: {
                  'x-token': localStorage.getItem('token')
                }
              });
              console.log(resp);
              swal("Bien!", 'Vecino eliminado', "success");
            } catch (error) {
              swal("Error!", "Al parecer no tiene privilegios", "error");
            }
            break;
          default:
            swal("Cancelado!");
        }
      });
    // console.log(id_veci);
    // const { data } = await clienteAxios.post('/api/vecino/crearVecino', {}, {
    //   headers: {
    //     'x-token': localStorage.getItem('token') || ''
    //   }
    // });
    // if (data.ok) {
    //   swal("Perfecto!", 'El vecino ha sido eliminado', "success");
    // }
  };

  if (vecinos === undefined) return <h1 className="my-4 text-center bg-blue">CARGANDO VECINOS, POR FAVOR ESPERE...</h1>;

  return (
    <>
      <div className="content">
        <Row>
          <Col md="12">
            <h4 className="title"><i className="fas fa-user"></i> ID GUARDIA: {id}</h4>
            <Card>
              <CardBody>
                <Table className="tablesorter">
                  <thead className="text-primary">
                    <tr>
                      <th>ID VECINO</th>
                      <th>Direcci√≥n</th>
                      <th>Nombre Contacto 1</th>
                      <th>Numero Contacto 1</th>
                      <th>Nombre Contacto 2</th>
                      <th>Numero Contacto 2</th>
                      <th className="text-center">Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    {
                      vecinos.map(vecino => (
                        <ListVecinoFila
                          key={vecino.id_veci}
                          vecino={vecino}
                          handleInputDelete={handleInputDelete}
                          fetchVecinos={fetchVecinos}
                          setVecinos={setVecinos}
                        />
                      ))
                    }
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default ListVecino;
